mapper.select:
SELECT *
FROM PRODUCT
WHERE PRODUCTNO = #{ productNo }

SELECT PRODUCTSIZE, PRODUCTCOUNT
FROM PRODUCTSIZE
WHERE PRODUCTNO = #{ productNo }

mapper.update:
UPDATE PRODUCT SET
PRODUCTCATEGORY = #{ productCategory }, PRODUCTBRAND = #{ productBrand }, PRODUCTTHUMBNAIL = #{ productThumbNail }, PRODUCTNAME = #{ productName }, PRODUCTPRICE = #{ productPrice }, PRODUCTDETAIL = #{ productDetail }, STATUS = #{ status }
PRODUCTCATEGORY = #{ productCategory }, PRODUCTBRAND = #{ productBrand }, PRODUCTNAME = #{ productName }, PRODUCTPRICE = #{ productPrice }, PRODUCTDETAIL = #{ productDetail }, STATUS = #{ status }
WHERE PRODUCTNO = #{ productNo }
